import myaxios from '../service/axios.service'
import {getRequest ,postRequest} from '../service/axios.service'
import store from '../store';

export const createHero = (hero) => {
   return postRequest("heroes/create", hero);
}

export const updateHero = (hero) => {
   return myaxios.put("heroes/update?org-secret=" + store.state.mdpOrga, hero);
}

export const getHeroById = (idHero) => {
   return getRequest("heroes/getbyid/" + idHero + "?org-secret=" + store.state.mdpOrga);
}

export const getHeroAliases = () => {
   return getRequest("hero/getaliases");
}
